import React from "react";
import { Typography, Stack, Button } from "@mui/material";

import bodypartimg from "../assets/icons/body-part.png";
import targetimg from "../assets/icons/target.png";
import equipmentimg from "../assets/icons/equipment.png";

const Detail = ({ exerciseDetail }) => {
  const { bodyPart, gifUrl, name, target, equipment } = exerciseDetail;
  
  const extraDetail = [
        { icon: bodypartimg, name: bodyPart },
        { icon: targetimg, name: target },
        { icon: equipmentimg, name: equipment }
    ];

  return (
    <Stack
      gap="60"
      sx={{ flexDirection: { lg: "row" }, p: "20px", alignItems: "center" }}
    >
      <img src={gifUrl} alt={name} loading="lazy" className="detail-image" />
      <Stack sx={{ gap: { lg: "35px", xs: "20px" } }}>
        <Typography variant="h4" mb={-2} fontWeight="bold">
          {name}
        </Typography>

        <Typography variant="h6" width={500}>
          Get Moving! Regular exercise boosts your physical and mental health,
          enhancing energy and mood. Set clear goals, stay consistent, and enjoy
          the journey to a healthier you!{" "}
        </Typography>
        <Typography variant="h6" mb={-2} fontWeight='bold'>Recommended For</Typography>

        {extraDetail.map((item)=>(
            <Stack key={item.id} direction="row" gap="20px" alignItems="center">
                <Button>
                    <img src={item.icon} alt={item.name}/>
                </Button>
                
            </Stack>
        ))}

        {/* <Stack direction="row">
          <Button
            sx={{
              // ml: "21px",
              color: "#000",
              background: "#ffa9a9",
              fontSize: "14px",
              borderRadius: "20px",
              textTransform: "capitalize",
              fontStyle: "italic",
            }}
          >
            {bodyPart}
          </Button>
          <Button
            sx={{
              ml: "21px",
              color: "#000",
              background: "#fcc757",
              fontSize: "14px",
              borderRadius: "20px",
              textTransform: "capitalize",
              fontStyle: "italic",
            }}
          >
            {target}
          </Button>
        </Stack> */}
      </Stack>
    </Stack>
  );
};

export default Detail;
