import React from "react";
import { Typography, Box, Stack } from "@mui/material";

const ExerciseVideos = ({ exerciseVideos, name }) => {

  console.log(exerciseVideos);

  if (!exerciseVideos.length)
    return (
      <Typography
        fontStyle={"italic"}
        fontWeight={400}
        display={"flex"}
        alignItems={"center"}
        justifyContent={"center"}
        fontSize={30}
        color={"lightseagreen"}
        mt={10}
      >
        Loading Exercise Videos...
      </Typography>
    );
  return (
    <Box
      sx={{
        mt: { lg: "270px", xs: "50px" }

      }}
      p="20px"
      sx={{flexDirection: { lg: "row" }, p: "50px", alignItems: "center" , justifyContent:'center', backgroundColor:'whitesmoke', margin:'0 100px', borderRadius:'30px', gap:'150px', border:'1px solid #fcc275'}}
    >
      <Typography display={'flex'} alignItems={'center'} justifyContent={'center'} variant="h4" mb="43px">
        Watch{" "}
        <span style={{ color: "#ff2625", textTransform: "capitalize" }}>
          {name}
        </span>{" "}
        Exercise Videos
      </Typography>

      <Stack
        flexWrap={"wrap"}
        alignItems={"center"}
        justifyContent={"flex-start"}
        SX={{
          flexDirection: { lg: "row" },
          gap: { lg: "110px", xs: "20px" },
          flexWrap: { lg: "wrap", xs: "nowrap" },
          px: { lg: "20px", xs: "0px" },
          borderRadius: "4px",
        }}
      >
        {exerciseVideos?.slice(0, 3).map((item, index) => (
          <a
            key={index}
            className="exercise-video"
            href={`https://www.youtube.com/watch?v=${item.video.videoId}`}
            target="_blank"
            rel="noreferrer"
          >
            <img src={item.video.thumbnails[0].url} />
          </a>
        ))}
      </Stack>
    </Box>
  );
};

export default ExerciseVideos;
